public void foo1 (int a) {
    if (a < 3) {
        while (a < 3) {
            a = a + 1;
            System.out.println ("foo");
        }
    }
}

public int foo2 (int a, int b) {
    if (a > b) {
        b = a;
    }
    return a;
}

public int foo3 (int a) {
    for (int i = 0;
    i < a; i ++) {
        a = a + 1;
    }
    return a;
}

