public double Type1a_Krawitz (int n) {
    int p = - 1;
    int sum = 0;
    for (p = 0; p < n; p ++) {
        sum += p;
    }
    if (n == 0) return sum;
    else return sum / n;

}

public double Type1b_Krawitz (int n) {
    int p = - 1;
    int sum = 0;
    for (p = 0; p < n; p ++) sum += p;

    if (n == 0) return sum;
    else return sum / n;

}

