void sumProd3C (int n) {
    double sum = 0.0;
    double prod = 1.0;
    for (int i = 1;
    i <= n; i ++) {
        sum = sum + i;
        prod = prod * i;
        if ((n % 2) == 0) {
            foo (sum, prod);
        }
    }
}

