public void sumProd3E (int n) {
    double sum = 0.0;
    double prod = 1.0;
    for (int i = 1;
    i <= n; i ++) {
        if (i % 2 == 0) {
            sum += i;
        }
        prod = prod * i;
        foo (sum, prod);
    }
}

void sumProd4A (int n) {
    double prod = 1.0;
    double sum = 0.0;
    for (int i = 1;
    i <= n; i ++) {
        sum = sum + i;
        prod = prod * i;
        foo (sum, prod);
    }
}

void sumProd4B (int n) {
    double sum = 0.0;
    double prod = 1.0;
    for (int i = 1;
    i <= n; i ++) {
        prod = prod * i;
        sum = sum + i;
        foo (sum, prod);
    }
}

void sumProd4C (int n) {
    double sum = 0.0;
    double prod = 1.0;
    for (int i = 1;
    i <= n; i ++) {
        sum = sum + i;
        foo (sum, prod);
        prod = prod * i;
    }
}

void sumProd4D (int n) {
    double sum = 0.0;
    double prod = 1.0;
    int i = 0;
    while (i <= n) {
        sum = sum + i;
        prod = prod * i;
        foo (sum, prod);
        i ++;
    }
}

private double foo (double sum, double prod) {
    return sum + prod + 1;
}

private double foo (double sum) {
    return sum + 1.0;
}

private double foo (double sum, double prod, double temp) {
    return sum + prod + temp;
}
